function [Pinv,D1] = sbp_sparse(acc,N,dx)
% function [Pinv,D1] = sbp_sparse(acc,N,dx)
% Load SBP operators of the given order.

if acc==2
    
    V = dx*[0.5 ones(1,N-1) 0.5];
    Pinv = spdiags((1./V)',0,N+1,N+1);
    
    d = ones(N+1,1)*[-1/2 0 1/2];
    Q = spdiags(d,[-1:1],N+1,N+1);

    Q(1,1) = -0.5;
    Q(end,end) = 0.5;
    
    D1 = sparse(Pinv*Q);
 
end

